// comments
import React, { useState, useEffect } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import Comments from "../components/commentpage/Comments";
import Navbar from "../components/NavBar.js";
import NewComment from "../components/commentpage/NewComment";
import Dropdown from "react-bootstrap/Dropdown";

const CommentPage = () => {
  const params = useParams();
  const navigate = useNavigate();
  const [comments, setComments] = useState([]);
  const [post, setPost] = useState([]);
  const [newComment, setNewComment] = useState("");
  const [orderIndex, setOrder] = useState(0);
  const [search, setSearch] = useState("");
  const order = ["Newest to oldest", "Oldest to newest"];

  useEffect(() => {
    let token = localStorage.getItem("token");
    const showPostURL = `http://localhost:3000/posts/${params.id}`;
    if (token) {
      fetch(showPostURL, {
        method: "GET",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
        .then((response) => {
          if (response.ok) {
            return response.json();
          }
          throw new Error("Network response was not ok.");
        })
        .then((response) => setPost(response));
    }
  }, []);

  useEffect(() => getComments(), [search, orderIndex]);

  const getComments = () => {
    const url = `http://localhost:3000/comments/${params.id}?order=${orderIndex}&search=${search}`;
    let token = localStorage.getItem("token");
    if (token) {
      fetch(url, {
        method: "GET",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
        .then((res) => {
          if (res.ok) {
            return res.json();
          }
          throw new Error("Network response was not ok.");
        })
        .then((res) => {
          setComments(res);
        })
        .catch(() => navigate("/login"));
    } else {
      navigate("/login");
    }
  };

  const handleOrder = (id) => {
    setOrder(id);
  };

  const handleSearch = (e) => {
    setSearch(e.target.value);
  };

  const stripHtmlEntities = (str) => {
    return String(str)
      .replace(/\n/g, "<br> <br>")
      .replace(/</g, "&lt;")
      .replace(/>/g, "&gt;");
  };

  const onSubmit = (event) => {
    event.preventDefault();
    const url = `http://localhost:3000/comments`;

    if (newComment.length == 0) return;

    const info = {
      comment: {
        post_id: params.id,
        body: stripHtmlEntities(newComment),
      },
    };

    let token = localStorage.getItem("token");
    if (token) {
      fetch(url, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(info),
      })
        .then((response) => {
          if (response.ok) {
            return response.json();
          }
          throw new Error("Network response was not ok.");
        })
        .then((res) => {
          setComments(res);
          console.log(res);
        })
        .catch((error) => console.log(error.message));
    }
  };

  const deleteComment = (id) => {
    const url = `http://localhost:3000/comments/${id}`;
    const auth_token = localStorage.getItem("token");
    console.log(url);
    fetch(url, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${auth_token}`,
      },
    })
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
        throw new Error("Network response was not ok.");
      })
      .then(() => getComments())
      .catch((error) => console.log(error.message));
  };

  return (
    <div>
      <Navbar user={localStorage.getItem("username")} />
      <main className="container">
        <div key={post.id} className="col-md-6 col-lg-4">
          <div className="card mb-4">
            <div className="card-body">
              <h5 className="card-title">{post.user}</h5>
              <h5 className="card-title">{post.title}</h5>
              <h5 className="card-title">{post.body}</h5>
            </div>
          </div>
        </div>
        <div className="text-end mb-3">
          <NewComment
            onSubmit={onSubmit}
            onChange={setNewComment}
            comment={newComment}
          />
        </div>
        <hr />
        <div className="container-row">
          <h3>Posts</h3>
          <form className="form-input">
            <input
              className="post-input"
              type="text"
              placeholder="Search here"
              onChange={handleSearch}
              value={search}
            />
          </form>
          <Dropdown>
            <Dropdown.Toggle id="dropdown-basic">
              {order[parseInt(orderIndex)]}
            </Dropdown.Toggle>

            <Dropdown.Menu>
              <Dropdown.Item onClick={() => handleOrder(0)}>
                Newest to oldest
              </Dropdown.Item>
              <Dropdown.Item onClick={() => handleOrder(1)}>
                Oldest to newest
              </Dropdown.Item>
            </Dropdown.Menu>
          </Dropdown>
        </div>
        <br />
        <Comments comments={comments} delete={deleteComment} />
      </main>
    </div>
  );
};

export default CommentPage;
