import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import Card from "react-bootstrap/Card";

const deletePost = (id) => {
  const url = `/api/v1/posts/destroy/${id}`;
  const token = document.querySelector('meta[name="csrf-token"]').content;

  fetch(url, {
    method: "DELETE",
    headers: {
      "X-CSRF-Token": token,
      "Content-Type": "application/json",
    },
  })
    .then((response) => {
      if (response.ok) {
        return response.json();
      }
      throw new Error("Network response was not ok.");
    })
    .then(() => window.location.reload(false))
    .catch((error) => console.log(error.message));
};

const Posts = (prop) => {
  const navigate = useNavigate();

  const allPosts = prop.posts.map((post, index) => (
    <div key={index} className="card-container">
      <div className="card-body">
        <div className="post-top">
          <h3 className="card-title">
            <a href={`/post/${post.id}`}>{post.title}</a>
          </h3>
        </div>
        <h5 className="card-subtitle mb-2 text-muted">{post.user.username}</h5>
        <h4 className="card-text">{post.body}</h4>
      </div>
    </div>
  ));
  const noPost = (
    <div className="vw-100 vh-50 d-flex align-items-center justify-content-center">
      <h4>No posts.</h4>
    </div>
  );

  return <div>{prop.posts.length > 0 ? allPosts : noPost}</div>;
};

export default Posts;
