import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import FloatingLabel from 'react-bootstrap/FloatingLabel';
import Container from "react-bootstrap/esm/Container";

const NewPost = prop => {
  const navigate = useNavigate();
  const formInfo = prop.info;

  const onChange = (event) => {
    prop.onChange({
      ...prop.info,
      [event.target.id] : event.target.value
  });
  };

  const handleSubmit = (event, title, body)=>{
    prop.onChange({
      title: '',
      body: ''
  });
    prop.submit(event, title, body);
  }


  return (
    <div className="post-form-container">
      <form className="post-form" onSubmit={(event)=>handleSubmit(event, formInfo.title, formInfo.body)}>
        <legend className="post-form-title">What's on your mind?</legend>
        <div className="form-floating mb-1">
          <input type="title" 
                  id="title" 
                  className="form-control" 
                  required 
                  onChange={(event) => onChange(event)}
                  placeholder=""/>
          <label className="form-label">Title</label>
        </div>
        <div className="form-floating">
          <input type="body" 
                  id="body" 
                  className="form-control" 
                  required 
                  onChange={(event) => onChange(event)}
                  placeholder=""/>
          <label className="form-label">Body</label>
        </div>
        <Button type="submit" className="btn mt-2" variant="light" disabled={!(formInfo.title && formInfo.body)}>
          Post
        </Button>
      </form>
    </div>
  );
};

export default NewPost;
