import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import NewPost from "./NewPost";

const deletePost = (id) => {
  const url = `/api/v1/posts/destroy/${id}`;
  const token = document.querySelector('meta[name="csrf-token"]').content;

  fetch(url, {
    method: "DELETE",
    headers: {
      "X-CSRF-Token": token,
      "Content-Type": "application/json",
    },
  })
    .then((response) => {
      if (response.ok) {
        return response.json();
      }
      throw new Error("Network response was not ok.");
    })
    .then(()=>window.location.reload(false))
    .catch((error) => console.log(error.message));
};

const Posts = prop => {
    const navigate = useNavigate();

  const allPosts = prop.posts.map((post, index) => (
    <div key={index} className="container-md">
      <div className="card mb-4">
        <div className="dropdown">
          <a className="btn btn-default btn-sm dropdown-toggle float-right" href="#" role="button" id="dropdownMenuLink" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
          </a>

          <div className="dropdown-menu" aria-labelledby="dropdownMenuLink">
            <a className="dropdown-item" onClick={()=>deletePost(post.id)}>Edit post</a>
            <a className="dropdown-item" onClick={()=>deletePost(post.id)}>Delete post</a>
          </div>
        </div>
          <div className="card-body">
            <a href={`/post/${post.id}`} className="stretched-link">
              <h3 className="card-title">{post.title}</h3>
            </a>
            <h5 className="card-subtitle mb-2 text-muted">{post.name}</h5>
            <h4 className="card-text">{post.body}</h4>
          </div>

      </div>
    </div>

  ));
  const noPost = (
    <div className="vw-100 vh-50 d-flex align-items-center justify-content-center">
      <h4>
        No posts yet. Why not <Link to="/new_post">create one</Link>
      </h4>
    </div>
  );

  return (
    <>
      <div className="py-5">
        <main className="container">
          <div className="text-end mb-3">
            {NewPost()}
          </div>
          <div className="row">
            {prop.posts.length > 0 ? allPosts : noPost}
          </div>
        </main>
      </div>
    </>
  );
};

export default Posts;
