// comments 
import React, { useState, useEffect } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import Comments from "../components/commentpage/Comments";
import Navbar from '../components/NavBar.js';
import NewComment from "../components/commentpage/NewComment";

const CommentPage = () => {
  const params = useParams();
  const navigate = useNavigate();
  const [comments, setComments] = useState([]);
  const [post, setPost] = useState([]);

  useEffect(() => {
    let token = localStorage.getItem("token")
    const commentURL = `http://localhost:3000/comments/${params.id}`;
    const showPostURL = `http://localhost:3000/posts/${params.id}`
    if (token){
        fetch(showPostURL, {
            method: "GET",
            headers: {
            "Authorization": `Bearer ${token}`
            }
        })
        .then((response) => {
        if (response.ok) {
            return response.json();
        }
        throw new Error("Network response was not ok.");
        })
        .then((response) => setPost(response));

        fetch(commentURL, {
            method: "GET",
            headers: {
            "Authorization": `Bearer ${token}`
            }
        })
        .then((response) => {
            if (response.ok) {
            return response.json();
            }
            throw new Error("Network response was not ok.");
        })
        .then((response) => setComments(response));
    }
  }, []);

  const onSubmit = (event) => {
      event.preventDefault();
      const url = "http://localhost:3000/posts";

      if (title.length === 0 || body.length === 0)
        return;
      const info = {
        post: {
          title,
          body: stripHtmlEntities(body),
        }
      };

      console.log(JSON.stringify(info))
      const auth_token = localStorage.getItem('token');
      fetch(url, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${auth_token}`
        },
        body: JSON.stringify(info),
      })
        .then((response) => {
          if (response.ok) {
            return response.json();
          }
          throw new Error("Network response was not ok.");
        })
        .then(()=>window.location.reload(false))
        .catch((error) => console.log(error.message));
        console.log("3")
    };

  return(
    <div>
      <Navbar user={localStorage.getItem('username')}/>
      <main className="container">
        <div key={post.id} className="col-md-6 col-lg-4">
          <div className="card mb-4">
            <div className="card-body">
              <h5 className="card-title">{post.name}</h5>
              <h5 className="card-title">{post.title}</h5>
              <h5 className="card-title">{post.body}</h5>
            </div>
          </div>
        </div>
        <div className="text-end mb-3">
            <NewComment id ={params.id} onSubmit={onSubmit}/>
        </div>
          <Comments comments = {comments}/>
        </main>
    </div>

  )

};

export default CommentPage;
