import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Posts from "../components/postpage/Posts";
import Navbar from "../components/NavBar.js";
import NewPost from "../components/postpage/NewPost";
import Dropdown from "react-bootstrap/Dropdown";

const HomePage = () => {
  let navigate = useNavigate();
  const [userInfo, setInfo] = useState({
    user: {},
    error: "",
  });
  const [formInfo, setFormInfo] = useState({
    title: "",
    body: "",
  });

  const order = ["Newest to oldest", "Oldest to newest"];
  const [posts, setPosts] = useState([]);
  const [orderIndex, setOrder] = useState(1);
  const [search, setSearch] = useState("");

  // useEffect(() => {
  //   console.log('hit');
  //   let token = localStorage.getItem('token');
  //   if(token){
  //     fetch('http://localhost:3000/profile', {
  //       method: "GET",
  //       headers: {
  //         "Authorization": `Bearer ${token}`
  //       }
  //     })
  //     .then(response => response.json())
  //     .then(result => {
  //       if(result.id){
  //         setInfo({
  //           ...userInfo,
  //           user: result
  //         })
  //       }
  //     })
  //   }
  // }, []
  // );
  const getPosts = () => {
    const url = "http://localhost:3000/posts";
    let token = localStorage.getItem("token");
    if (token) {
      fetch(url, {
        method: "GET",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
        .then((res) => {
          if (res.ok) {
            return res.json();
          }
          throw new Error("Network response was not ok.");
        })
        .then((res) => {
          setPosts({ res });
        })
        .catch(() => navigate("/login"));
    } else {
      navigate("/login");
    }
  };

  useEffect(() => getPosts(), [order]);

  const onSubmit = (event, title, body) => {
    event.preventDefault();
    const url = "http://localhost:3000/posts";

    if (title.length === 0 || body.length === 0) return;
    const info = {
      post: {
        title,
        body: stripHtmlEntities(body),
      },
    };
    setFormInfo({
      title: "",
      body: "",
    });

    const auth_token = localStorage.getItem("token");
    fetch(url, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${auth_token}`,
      },
      body: JSON.stringify(info),
    })
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
        throw new Error("Network response was not ok.");
      })
      .then(() => {
        getPosts();
      })
      .catch((error) => console.log(error.message));
  };

  const stripHtmlEntities = (str) => {
    return String(str)
      .replace(/\n/g, "<br> <br>")
      .replace(/</g, "&lt;")
      .replace(/>/g, "&gt;");
  };

  return (
    <div>
      <Navbar user={localStorage.getItem("username")} />
      <div className="py-5">
        <main className="container">
          <div className="text-end mb-3">
            <NewPost submit={onSubmit} onChange={setFormInfo} info={formInfo} />
          </div>
          <hr />
          <div className="container">
            <h3>Posts</h3>
            <Dropdown>
              <Dropdown.Toggle variant="success" id="dropdown-basic">
                Dropdown Button
              </Dropdown.Toggle>

              <Dropdown.Menu>
                <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
                <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
                <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
          </div>
          <br />
          <Posts posts={posts} />
        </main>
      </div>
    </div>
  );
};

export default HomePage;
