import React from "react";
import { useNavigate } from "react-router-dom";

const NewPost = (prop) => {
  const navigate = useNavigate();
  const formInfo = prop.info;

  const onChange = (event) => {
    prop.onChange({
      ...prop.info,
      [event.target.id]: event.target.value,
    });
  };

  const handleSubmit = (event, title, body) => {
    prop.submit(event, title, body);
  };

  return (
    <div className="post-form-container">
      <form
        className="post-form"
        onSubmit={(event) => handleSubmit(event, formInfo.title, formInfo.body)}
      >
        <legend className="post-form-title">EditPost</legend>

        <label className="form-label">Title</label>
        <input
          type="title"
          id="title"
          className="form-control"
          required
          value={formInfo.title}
          onChange={(event) => onChange(event)}
        />
        <label className="form-label">Body</label>
        <input
          type="body"
          id="body"
          className="form-control"
          required
          onChange={(event) => onChange(event)}
          value={formInfo.body}
        />

        <button
          type="submit"
          className="form-btn"
          disabled={!(formInfo.title && formInfo.body)}
        >
          Post
        </button>
      </form>
    </div>
  );
};

export default NewPost;
