import React, { useState } from 'react';
import {
  MDBContainer,
  MDBInput,
  MDBCheckbox,
  MDBBtn,
  MDBIcon
}
from 'mdb-react-ui-kit';

const SignIn = props => {
    const [userInfo, setInfo] = useState({
        username: '',
        password: ''
    });

    const handleChange = (event) => {

        setInfo({
            ...userInfo,
            [event.target.name] : event.target.value
        });
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        props.signIn(userInfo);
    }

    return (
        // <div>
        //     <form onSubmit={handleSubmit}>
        //         <h1>Sign In Form</h1>
        //         <label>Username :</label>
        //         <input name='username' value={userInfo['username']} onChange={handleChange}/>
        //         <label>Password :</label>
        //         <input type="password" name='password' value={userInfo['password']} onChange={handleChange}/>
        //         {props.error ? <p style={{color: 'red'}}>{props.error}</p> : null}
        //         <input type="submit" value="Authenticate"/>
        //         <h5>Don't have an account? <a href="/signup">Register</a></h5>
        //     </form>
        // </div>
        // <MDBContainer className="p-3 my-5 d-flex flex-column w-50">

        // <MDBInput wrapperClass='mb-4' label='Email address' id='form1' type='email'/>
        // <MDBInput wrapperClass='mb-4' label='Password' id='form2' type='password'/>

        // <div className="d-flex justify-content-between mx-3 mb-4">
        //   <MDBCheckbox name='flexCheck' value='' id='flexCheckDefault' label='Remember me' />
        //   <a href="!#">Forgot password?</a>
        // </div>

    <MDBContainer className="p-3 my-5 d-flex flex-column w-50">

      <MDBInput wrapperClass='mb-4' label='Username' id='form1' type='username'/>
      <MDBInput wrapperClass='mb-4' label='Password' id='form2' type='password'/>


      <MDBBtn className="mb-4">Sign in</MDBBtn>

      <div className="text-center">
        <p>Not a member? <a href="#!">Register</a></p>
        <p>or sign up with:</p>

        <div className='d-flex justify-content-between mx-auto' style={{width: '40%'}}>
          <MDBBtn tag='a' color='none' className='m-1' style={{ color: '#1266f1' }}>
            <MDBIcon fab icon='facebook-f' size="sm"/>
          </MDBBtn>

          <MDBBtn tag='a' color='none' className='m-1' style={{ color: '#1266f1' }}>
            <MDBIcon fab icon='twitter' size="sm"/>
          </MDBBtn>

          <MDBBtn tag='a' color='none' className='m-1' style={{ color: '#1266f1' }}>
            <MDBIcon fab icon='google' size="sm"/>
          </MDBBtn>

          <MDBBtn tag='a' color='none' className='m-1' style={{ color: '#1266f1' }}>
            <MDBIcon fab icon='github' size="sm"/>
          </MDBBtn>

        </div>
      </div>

    </MDBContainer>
    );
}

export default SignIn;
