  //   fetch(url, {
  //     method: "POST",
  //     headers: {
  //       "Content-Type": "application/json",
  //       "Authorization": `Bearer ${auth_token}`
  //     },
  //     body: JSON.stringify(info),
  //   })
  //     .then((response) => {
  //       if (response.ok) {
  //         return response.json();
  //       }
  //       throw new Error("Network response was not ok.");
  //     })
  //     .then(()=>window.location.reload(false))
  //     .catch((error) => console.log(error.message));
  //     console.log("3")
  // };


  return (
    <div className="post-form-container">
      <form className="post-form" onSubmit={(event)=>onSubmit(event, title, body)}>
        <legend className="post-form-title">What's on your mind?</legend>
        <div className="form-floating mb-1">
          <input type="title" 
                  id="title" 
                  className="form-control" 
                  required 
                  onChange={(event) => onChange(event, setTitle)}
                  placeholder=""/>
          <label className="form-label">Title</label>
        </div>
        <div className="form-floating">
          <input type="body" 
                  id="body" 
                  className="form-control" 
                  required 
                  onChange={(event) => onChange(event, setBody)}
                  placeholder=""/>
          <label className="form-label">Body</label>
        </div>
        <Button type="submit" className="btn mt-2" variant="light" disabled={!(title && body)}>
          Post
        </Button>
      </form>
    </div>
  );
};

export default NewPost;
