// comments 
import React, { useState, useEffect } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import NewComment from "./components/NewComment"
import Comments from "../components/Comments"

const CommentPage = () => {
  const params = useParams();
  const navigate = useNavigate();
  const [comments, setComments] = useState([]);
  const [post, setPost] = useState([]);

  useEffect(() => {
    let token = localStorage.getItem("token")
    const commentURL = `http://localhost:3000/comments/index/${params.id}`;
    const showPostURL = `http://localhost:3000/posts/show/${params.id}`
    if (token){
        fetch(showPostURL, {
            method: "GET",
            headers: {
            "Authorization": `Bearer ${token}`
            }
        })
        .then((response) => {
        if (response.ok) {
            return response.json();
        }
        throw new Error("Network response was not ok.");
        })
        .then((response) => setPost(response))
        .catch(() => navigate("/login"));

        fetch(commentURL, {
            method: "GET",
            headers: {
            "Authorization": `Bearer ${token}`
            }
        })
        .then((response) => {
            if (response.ok) {
            return response.json();
            }
            throw new Error("Network response was not ok.");
        })
        .then((response) => setComments(response))
        .catch(() => navigate("/login"));
    }
    else{
        navigate("/login");
    }
  }, [params.id]);

  const addHtmlEntities = (str) => {
    return String(str).replace(/&lt;/g, "<").replace(/&gt;/g, ">");
  };
  let allComments = "";
  if (comments.length > 0){
  allComments = comments.map((comment) => (
    <div key={comment.id} className="col-md-6 col-lg-4">
      <div className="card mb-4">
        <div className="card-body">
          <h5 className="card-title">{comment.name}</h5>
          <h5 className="card-title">{comment.body}</h5>
          <h5 className="card-title">{comment.post_id}</h5>

        </div>
      </div>
    </div>
  ));
  }
console.log(post);



  return (
    <>
      <div className="py-5">
        <main className="container">
        <div key={post.id} className="col-md-6 col-lg-4">
          <div className="card mb-4">
            <div className="card-body">
              <h5 className="card-title">{post.name}</h5>
              <h5 className="card-title">{post.title}</h5>
              <h5 className="card-title">{post.body}</h5>

            </div>
          </div>
          <div className="text-end mb-3">
            {NewComment(params.id)}
          </div>
        </div>
          <div className="row">
            {allComments}
          </div>
        </main>
      </div>
    </>
  );
};

export default CommentPage;
