import React, { useState, useEffect } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";

const Comments = (prop) => {
  const navigate = useNavigate();
  let allComments = "No Comments";
  if (comments.length > 0) {
    allComments = prop.comments.map((comment, index) => (
      <div key={index} className="card-container">
        <div className="card">
          <div className="card-body">
            <div className="card-top-container">
              <h5 className="card-subtitle text-muted">
                {comment.user.username} | Posted{" "}
                {getTimeElapsed(post.updated_at)} ago
              </h5>
              <Dropdown className="card-dropdown">
                <Dropdown.Toggle
                  className="card-dropdown"
                  variant="secondary"
                  size="sm"
                ></Dropdown.Toggle>

                <Dropdown.Menu>
                  <Dropdown.Item>Edit Comment</Dropdown.Item>
                  <Dropdown.Item a-key={comment.id}>Delete Post</Dropdown.Item>
                </Dropdown.Menu>
              </Dropdown>
            </div>
            <h3 className="card-body">{comment.body}</h3>
          </div>
        </div>
      </div>
    ));
  }
  return <div>{allComments}</div>;
};

export default Comments;
